import { TaskItem } from "./types";

const API_URL = "http://localhost:1337/api/tasks";

export async function fetchTasks(): Promise<TaskItem[]> {
  try {
    const response = await fetch(API_URL);
    const data = await response.json();
    return data.data.map((item: any) => ({
      id: item.attributes.taskId, // Используем taskId из Strapi
      text: item.attributes.text,
      completed: item.attributes.completed,
    }));
  } catch (error: any) {
    console.error("Error while fetching tasks:", error);
    throw new Error("Failed to fetch tasks. Please try again later.");
  }
}

export async function handleAddTask(taskText: string): Promise<TaskItem> {
  try {
    const response = await fetch(API_URL, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ data: { text: taskText, completed: false } }),
    });
    const newTask = await response.json();
    return newTask.data; // Возвращаем данные новой задачи
  } catch (error: any) {
    console.error("Error adding task:", error);
    throw new Error("Failed to add task. Please try again later.");
  }
}


export async function updateTask(id: number, updatedTask: TaskItem): Promise<void> {
  try {

    await fetch(`${API_URL}/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({data: updatedTask})
    });

  } catch (error) {
    console.error('Error updating task:', error);
    throw new Error('Failed to update task. Please try again later.')
  }
}




export async function handleDeleteTask(id: number): Promise<void> {
  try {
    await fetch(`${API_URL}/${id}`, {
      method: "DELETE",
    });
  } catch (error: any) {
    console.error("Error deleting task:", error);
    throw new Error("Failed to delete task. Please try again later.");
  }
}
