const API_URL = "http://localhost:1337/api/tasks";

const headers = {
  'Content-Type': 'application/json',
  // 'Authorization': 'Bearer YOUR_TOKEN' // если нужна авторизация
};

export async function fetchTasks(): Promise<TaskItem[]> {
  try {
    const response = await fetch(API_URL);
    const data = await response.json();
    return data.data.map((item: any) => ({
      id: item.id,
      text: item.text,
      completed: item.completed,
    }));
  } catch (error: any) {
    console.error("Error while fetching tasks:");
    throw new Error("Failed to fetch tasks. Please try again later.");
  }
}

export async function handleAddTask(taskText: string): Promise<TaskItem> {
  try {
    const response = await fetch(API_URL, {
      method: "POST",
      headers,
      body: JSON.stringify({
        data: {
          text: taskText,
          completed: false,
        }
      }),
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const result = await response.json();
    return {
      id: result.data.id,
      text: result.data.attributes.text,
      completed: result.data.attributes.completed,
    };
  } catch (error: any) {
    console.error("Error adding task:", error);
    throw new Error("Failed to add task. Please try again later.");
  }
}

export async function handleDeleteTask(id: number): Promise<void> {
  try {
    const deleteResponse = await fetch(`${API_URL}/${id}`, {
      method: "DELETE",
      headers,
    });

    if (!deleteResponse.ok) {
      throw new Error(`HTTP error! status: ${deleteResponse.status}`);
    }
  } catch (error: any) {
    console.error("Error deleting task:", error);
    throw new Error("Failed to delete task. Please try again later.");
  }
}

export async function updateTask(
  id: number,
  completed: boolean,
  text: string
): Promise<void> {
  try {
    const updateResponse = await fetch(`${API_URL}/${id}`, {
      method: "PUT",
      headers,
      body: JSON.stringify({
        data: {
          text,
          completed,
        }
      }),
    });

    if (!updateResponse.ok) {
      throw new Error(`HTTP error! status: ${updateResponse.status}`);
    }
  } catch (error) {
    console.error("Error updating task:", error);
    throw new Error("Failed to update task. Please try again later.");
  }
}
