import { TaskItem } from "./types";

const API_URL = "http://localhost:1337/api/tasks";

export async function fetchTasks(): Promise<TaskItem[]> {
  try {
    const response = await fetch(API_URL);
    const data = await response.json();
    return data.data.map((item: any) => ({
      id: item.id,
      text: item.attributes.text,
      completed: item.attributes.completed,
    }));
  } catch (error: any) {
    console.error("Error while fetching tasks:");
    throw new Error("Failed to fetch tasks. Please try again later.");
  }
}

export async function handleAddTask(taskText: string): Promise<TaskItem> {
  try {
    const response = await fetch(API_URL, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ data: { text: taskText, completed: false } }),
    });
    if (!response.ok) {
      throw new Error(`Network response was not ok: ${response.status}`);
    }
    const newTaskData = await response.json();
    return {
      id: newTaskData.data.id,
      text: newTaskData.data.text,
      completed: newTaskData.data.completed,
    };
  } catch (error: any) {
    console.error("Error adding task:", error);
    throw new Error("Failed to add task. Please try again later.");
  }
}

export async function updateTask(updatedTask: TaskItem): Promise<void> {
  try {
    const response = await fetch(`${API_URL}/${updatedTask.id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ data: { text: updatedTask.text, completed: updatedTask.completed } }),
    });
    if (!response.ok) {
      throw new Error(`Network response was not ok: ${response.status}`);
    }
  } catch (error) {
    console.error("Error updating task:", error);
    throw new Error("Failed to update task. Please try again later.");
  }
}

export async function deleteTask(taskId: number): Promise<void> {
  try {
    const response = await fetch(`${API_URL}/${taskId}`, {
      method: "DELETE",
    });
    if (!response.ok) {
      throw new Error(`Network response was not ok: ${response.status}`);
    }
  } catch (error) {
    console.error("Error deleting task:", error);
    throw new Error("Failed to delete task. Please try again later.");
  }
}

export async function markAllCompleted(): Promise<void> {
  try {
    const tasks = await fetchTasks();
    const updatePromises = tasks.map(task => 
      updateTask({ ...task, completed: true })
    );
    await Promise.all(updatePromises);
  } catch (error) {
    console.error("Error marking all tasks as completed:", error);
    throw new Error("Failed to mark all tasks as completed. Please try again later.");
  }
}

export async function deleteCompleted(): Promise<void> {
  try {
    const tasks = await fetchTasks();
    const deletePromises = tasks
      .filter(task => task.completed)
      .map(task => deleteTask(task.id));
    await Promise.all(deletePromises);
  } catch (error) {
    console.error("Error deleting completed tasks:", error);
    throw new Error("Failed to delete completed tasks. Please try again later.");
  }
}
