import { TaskItem } from "./types";

const API_URL = "http://localhost:1337/api/tasks";

export async function fetchTasks(): Promise<TaskItem[]> {
  try {
    const response = await fetch(API_URL);
    if (!response.ok) {
      throw new Error("Failed to fetch tasks.");
    }
    const data = await response.json();
    return data.data.map((item: any) => ({
      id: item.id,
      text: item.text,
      completed: item.completed,
    }));
  } catch (error: any) {
    console.error("Error while fetching tasks:", error);
    throw new Error("Failed to fetch tasks. Please try again later.");
  }
}

export async function handleAddTask(taskText: string): Promise<TaskItem> {
  const response = await fetch(API_URL, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({ data: { text: taskText, completed: false } }),
  });

  if (!response.ok) {
    throw new Error("Failed to add task.");
  }

  const newTaskData = await response.json();
  const newTask = newTaskData.data;

  return {
    id: newTask.id,
    text: newTask.text,
    completed: newTask.completed,
  };
}

export async function handleDeleteTask(id: number): Promise<void> {
  try {
    const response = await fetch(`${API_URL}/${id}`, {
      method: "DELETE",
    });

    if (!response.ok) {
      throw new Error("Failed to delete task.");
    }
  } catch (error: any) {
    console.error("Error deleting task:", error);
    throw new Error("Failed to delete task. Please try again later.");
  }
}

export async function updateTask(
  id: number,
  completed: boolean,
  text: string
): Promise<void> {
  const response = await fetch(`${API_URL}/${id}`, {
    method: "PUT",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({ data: { text, completed } }),
  });

  if (!response.ok) {
    throw new Error("Failed to update task.");
  }
}
