import { makeAutoObservable } from "mobx";
import { TaskItem } from "../types";
import { fetchTasks, handleAddTask, handleDeleteTask, updateTask as apiUpdateTask } from "../api";

const saveTasksToLocalStorage = (tasks: TaskItem[]): void => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
};

const getTasksFromLocalStorage = (): TaskItem[] => {
    const savedTasks = localStorage.getItem("tasks");
    try {
        return savedTasks ? JSON.parse(savedTasks) : [];
    } catch (error) {
        console.error("Error parsing saved tasks:", error);
        return [];
    }
};


class TodoStore {
    tasks: TaskItem[] = [];
    newTaskText: string = "";
    editingTask: TaskItem | null = null;
    message: string = "";

    constructor() {
        makeAutoObservable(this);
    }


    async fetchInitialTasks() {
        try {
            const fetchedTasks = await fetchTasks();
            this.tasks = fetchedTasks;
            this.message = "❗Tasks loaded from server❗";
        } catch (error) {
            console.error("Error fetching tasks:", error);
            this.message = "❗Error loading tasks❗";
        }
    }



    addTask = async () => {
        if (!this.newTaskText.trim()) return;

        try {
            const newTask = await handleAddTask(this.newTaskText);
            this.tasks.push(newTask);
            this.newTaskText = "";
        } catch (error) {
            this.message = "Failed to add task. Please try again later.";
            console.error(error);
        }
    };


    deleteTask = async (id: number) => {
        try {
            await handleDeleteTask(id);
            this.tasks = this.tasks.filter((task) => task.id !== id);
        } catch (error) {
            this.message = "Failed to delete task. Please try again later.";
            console.error(error);
        }
    };

    toggleTask = async (id: number) => {
        try {
            const taskIndex = this.tasks.findIndex((task) => task.id === id);
            if (taskIndex === -1) {
                return;
            }
            const updatedTask = { ...this.tasks[taskIndex], completed: !this.tasks[taskIndex].completed };
            await apiUpdateTask(id, updatedTask);
            this.tasks[taskIndex] = updatedTask;
        } catch (error) {
            this.message = "Failed to toggle task. Please try again later.";
            console.error(error);
        }
    };


    markAllCompleted = async () => {
        try {
            for (const task of this.tasks) {
                if (!task.completed) {
                    const updatedTask = { ...task, completed: true };
                    await apiUpdateTask(task.id, updatedTask);
                }
            }
            this.tasks = this.tasks.map((task) => ({ ...task, completed: true }));

        } catch (error) {
            this.message = "Failed to mark all tasks as completed. Please try again later.";
            console.error(error);
        }
    };

    deleteCompleted = async () => {
        try {
            const completedTasksIds = this.tasks.filter(task => task.completed).map(task => task.id);
            for (const taskId of completedTasksIds) {
                await handleDeleteTask(taskId);
            }


            this.tasks = this.tasks.filter((task) => !task.completed);
        } catch (error) {
            this.message = "Failed to delete completed tasks. Please try again later.";
            console.error(error);

        }
    };

    startEditingTask(task: TaskItem) {
        this.editingTask = task;
    }

    cancelEditingTask() {
        this.editingTask = null;
    }

    updateTask = async (updatedTask: TaskItem) => {
        if (updatedTask.text.trim() === "") return;

        try {
            await apiUpdateTask(updatedTask.id, updatedTask);

            const taskIndex = this.tasks.findIndex((task) => task.id === updatedTask.id);
            if (taskIndex !== -1) {
                this.tasks[taskIndex] = updatedTask;
            }
            this.editingTask = null;
        } catch (error) {
            this.message = "Failed to update task. Please try again later.";
            console.error(error);
        }
    };

    setNewTaskText(text: string) {
        this.newTaskText = text;
    }


}


export default new TodoStore();
